/**
 * @fileoverview Detects the usage of sap.ui.commons objects.
 * @author Christopher Fenner - D065687
 * @ESLint Version 0.8.0 / April 2016
 */
"use strict";

//------------------------------------------------------------------------------
// Requirements
//------------------------------------------------------------------------------

var rule = require("../../../lib/rules/sap-no-commons-usage.js"),

    RuleTester = require("eslint").RuleTester;


//------------------------------------------------------------------------------
// Tests
//------------------------------------------------------------------------------
var MSG = "Usage of sap.ui.commons controls is forbidden, please use controls from sap.m";
var ruleTester = new RuleTester();
ruleTester.run("sap-no-commons-usage", rule,              
                {
                    valid: [
                            "any.method(['sap/ui/commons/layout/MatrixLayoutCell'])"
                    ],
                    invalid: [
                            {
                                code: "function getLayout() { return new sap.ui.commons.layout.MatrixLayoutCell({ content : [ '' ], vAlign : 'Middle', hAlign : ''});}",
                                errors: [
                                    {
                                        message: MSG,
                                        type: "NewExpression"
                                    }
                                ]
                            },
                            {
                                code: "sap.ui.define(['sap/ui/commons/layout/MatrixLayoutCell'], function(MatrixLayout) {})",
                                errors: [
                                    {
                                        message: MSG,
                                        type: "CallExpression"
                                    }
                                ]
                            }
                    ]
                });
