/**
 * @fileoverview flag global variable declaration
 * @author Achref Kilani Jrad
 */
"use strict";

//------------------------------------------------------------------------------
// Requirements
//------------------------------------------------------------------------------

var rule = require("../../../lib/rules/sap-no-global-variable.js"),

    RuleTester = require("eslint").RuleTester;


//------------------------------------------------------------------------------
// Tests
//------------------------------------------------------------------------------

var ruleTester = new RuleTester();
ruleTester.run("sap-no-global-variable", rule,              
 {
    valid: [
            "function test(node){var node2 = 1, test = 'Apfelstrudel';};",
            "var Infinity = 5; var arguments = []",
            "var Infinity = 5, arguments = [];"
    ],
    invalid: [
        {
            code: "var global = true;",
            errors: [
                {
                    message: "Global variable 'global' is not allowed",
                    type: "Identifier"
                }
            ]
        }
    ]
});
