/**
 * @fileoverview Tests for sap-no-window-alert rule.
 * @author Achref Kilani Jrad - C5215143
 * @ESLint Version 0.14.0 / February 2015
 */

// ------------------------------------------------------------------------------
// Invoking global form of strict mode syntax for whole script
// ------------------------------------------------------------------------------
"use strict";


//------------------------------------------------------------------------------
// Requirements
//------------------------------------------------------------------------------

var rule = require("../../../lib/rules/sap-no-window-alert.js"),

    RuleTester = require("eslint").RuleTester;


//------------------------------------------------------------------------------
// Tests
//------------------------------------------------------------------------------

var ruleTester = new RuleTester();
ruleTester
        .run("sap-no-window-alert", rule, {
                    valid: [
                            "jQuery.sap.declare('js.windowAlert');",
                            "myMethod();"
                    ],
                    invalid: [
                        {
                            code: "window.alert('hello world');",
                            errors: [
                                {
                                    message: "A window.alert statement should not be part of the code that is committed to GIT! Use sap.m.MessageBox instead.",
                                    type: "CallExpression"
                                }
                            ]
                        }
                    ]
                });